import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import Login from '../components/Login';
import Dashboard from '../components/Dashboard';
import './LandingPage.css';
import Logo from '../assets/logo.jpg';

const LandingPage = () => {
  const [showPopup, setShowPopup] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const togglePopup = () => {
    setShowPopup(!showPopup);
  };

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  return (
    <div className="landing-page">
      <Navbar />
      {isLoggedIn ? (
        <Dashboard />
      ) : (
        <>
          <header className="landing-header">
            <img src={Logo} alt="QueueBy6 Logo" className="app-logo" />
            <h1>Welcome!</h1>
            <p>Your efficient solution for scheduling patient appointments.</p>
          </header>
          <div className="hero-section">
            <button onClick={togglePopup} className="hero-button">Get Started</button>
          </div>
          {showPopup && <Login closePopup={togglePopup} onLogin={handleLogin} />}
        </>
      )}
    </div>
  );
};

export default LandingPage;
